from algo8 import *from parser import *def deploy(stream, awc, filename):  a = 0.0  f = open('thresholdP','r')  thresholdP = float(f.read())  f.close()  inputTraffic = parse(filename)  windowCount = inputTraffic.size/stream.windowSize  attackWindowCount = 0.0  normalWindowCount = 0.0  state = []  winNo =[]  for i in range(windowCount):    p = determineProbability(inputTraffic[i*100:(i*100)+100], stream)    if ( p < thresholdP):      a += 1      attackWindowCount +=1      if a > awc:        a = awc    else:      a -= 1      normalWindowCount += 1      if a < 0:        a = 0    if a >= awc:      state = append(state,1)      winNo = append(winNo,i)    else:      state = append(state,0)      winNo = append(winNo,i)  stream.attackWindowCount = attackWindowCount  stream.normalWindowCount = normalWindowCount  stream.stateArr = state  stream.winNoArr = winNo  return stream